new Recommendation {Id = 845, Nom = @"Adopter des mesures concrètes et efficaces pour faire en sorte que les personnes handicapées, en particulier les femmes et les filles victimes de violence sexiste et les enfants victimes de mauvais traitements, aient accès à des services et à des informations, notamment des permanences téléphoniques, des refuges, des services d’appui aux victimes et des services de consultation et de conseil, ainsi qu’à des mécanismes de plainte qui soient chargés, entre autres, d’accorder des indemnisations aux victimes et d’imposer des sanctions contre les auteurs de ces actes ;", NomAr = @"-       (أ) اعتماد تدابير ملموسة وفعالة لضمان إمكانية وصول الأشخاص ذوي الإعاقة، ولا سيما النساء والفتيات ذوات الإعاقة من ضحايا العنف الجنساني والأطفال ذوي الإعاقة من ضحايا سوء المعاملة، إلى الخدمات والمعلومات، بما في ذلك خطوط الاتصال المباشر ومراكز الإيواء وخدمات دعم الضحايا، والمشورة والنصح، وإلى آليات تقديم الشكاوى المخولة، في جملة أمور، بتقديم تعويضات وفرض جزاءات على الجناة؛", CodeRecommendation = "R35P1", CodeRecommendationAr = "ت.35.ف. 1", Mecanisme = "Organes de traités", IdCycle = null, IdAxe = 6, IdSousAxe = 30, IdOrgane = 8, IdVisite = null, IdPays = null, Etat = "Non réalisé", EtatAvancement = "", EtatAvancementChiffre = 0, Observation = "", Complement = "", PieceJointe = "" },
